    <script>
        /* Default comment here */
        document.addEventListener("DOMContentLoaded", () => {
            if (
                window.location.search.includes("elementor-preview") &&
                window.parent.document.querySelector(
                    "#elementor-editor-wrapper"
                )
            ) {
                const leftEditorPanel =
                    window.parent.document.querySelector(
                        "aside#elementor-panel"
                    );

                var imageFileName = null;

                function extractImageFileName(imageElement) {
                    var imagePathArray =
                        imageElement.style.backgroundImage.match(
                            /url\(["'](.+?)["']\)/
                        );
                    if (imagePathArray.length >= 2) {
                        var imageFileName = imagePathArray[1]
                            .split("/")
                            .pop();
                        return imageFileName;
                    }
                    return null;
                }

                leftEditorPanel.addEventListener("click", function (e) {
                    if (
                        e.target.classList.contains(
                            "elementor-control-media-area"
                        )
                    ) {
                        const imageNameHolder = e.target.querySelector(
                            ".elementor-control-media__preview"
                        );
                        if (imageNameHolder) {
                            imageFileName =
                                extractImageFileName(imageNameHolder);
                            if (imageFileName) {
                                waitForMediaDialogAndPopulateSearchInput(
                                    imageFileName
                                );
                            }
                        }
                    } else if (
                        e.target.classList.contains(
                            "elementor-control-media__preview"
                        )
                    ) {
                        const imageNameHolder = e.target;
                        if (imageNameHolder) {
                            imageFileName =
                                extractImageFileName(imageNameHolder);
                            if (imageFileName) {
                                waitForMediaDialogAndPopulateSearchInput(
                                    imageFileName
                                );
                            }
                        }
                    } else if (
                        e.target.classList.contains(
                            "elementor-control-media__replace"
                        )
                    ) {
                        const closestControlMediaPreview = e.target.closest(
                            ".elementor-control-media__content"
                        );
                        const imageNameHolder =
                            closestControlMediaPreview.querySelector(
                                ".elementor-control-media__preview"
                            );
                        if (imageNameHolder) {
                            imageFileName =
                                extractImageFileName(imageNameHolder);
                            if (imageFileName) {
                                waitForMediaDialogAndPopulateSearchInput(
                                    imageFileName
                                );
                            }
                        }
                    }
                });

                function waitForMediaDialogAndPopulateSearchInput(
                    imageFileName
                ) {
                    var checkInterval = setInterval(function () {
                        var searchInput =
                            window.parent.document.querySelector(
                                '.media-modal[role="dialog"] .media-toolbar-primary input#media-search-input.search'
                            );
                        if (searchInput) {
                            searchInput.value = imageFileName;
                            var inputEvent = new Event("input", {
                                bubbles: true,
                            });
                            searchInput.dispatchEvent(inputEvent);
                            clearInterval(checkInterval);
                        }
                    }, 100);
                }
            }
        });
    </script>
